import {PhotosType} from './usersReducer';
import {profileAPI} from '../api/api';
import {Dispatch} from 'redux';

export type PostDataType = {
    id: number
    message: string
    likesCount: number
}

type ContactsType = {
    'facebook': string
    'website': null
    'vk': string
    'twitter': string
    'instagram': string
    'youtube': null
    'github': string
    'mainLink': null
}


export type ProfileDaTaType = {
    aboutMe: string
    contacts: ContactsType
    lookingForAJob: boolean
    lookingForAJobDescription: string
    fullName: string
    userId: number
    photos: PhotosType
}

export type InitialProfileStateType = {
    postData: PostDataType[]
    newPostText: string
    profile: ProfileDaTaType
    status: string
}

let initialProfileState = {
    postData: [
        {id: 1, message: 'НЕ СТАВИТЬ НОВЫЕ БИБЛИОТЕКИ!!!!', likesCount: 5},
        {id: 2, message: 'НЕ СТАВИТЬ НОВЫЕ БИБЛИОТЕКИ!!!!', likesCount: 15},

    ],
    newPostText: '',
    profile: {
        aboutMe: '12345789',
        contacts: {
            facebook: 'facebook.com',
            website: null,
            vk: 'vk.com/@vlad',
            twitter: 'https://twitter.com/@sdf',
            instagram: 'instagra.com/sds',
            youtube: null,
            github: 'github.com',
            mainLink: null
        },
        lookingForAJob: true,
        lookingForAJobDescription: 'ищу работу ',
        fullName: 'Влад',
        userId: 23422,
        photos: {
            small: 'data:image/jpeg;base64,',
            large: 'data:image/jpeg;base64,'
        }
    },
    status: 'учус, хочу скорее на работу'
}

export const profileReducer = (state = initialProfileState, action: AddPostType): InitialProfileStateType => {
    switch (action.type) {
        case 'ADD-POST': {
            const newPost: PostDataType = {
                id: 5,
                message: action.postPost,
                likesCount: 0,
            }
            return {
                ...state,
                postData: [...state.postData, newPost],
                newPostText: '',

            }
        }
        case 'UPDATE-NEW-POST-TEXT': {
            return {
                ...state,
                newPostText: action.newText,
            }
        }
        case 'SET-USER-PROFILE': {
            return {
                ...state,
                profile: action.profile
            }
        }
        case 'SET-USER-STATUS':
            return {
                ...state,
                status: action.status
            }
        default:
            return state
    }
}

export type AddPostType = ReturnType<typeof addPostAC>
    | ReturnType<typeof onPostChangeAC>
    | ReturnType<typeof setUserProfile>
    | ReturnType<typeof setStatus>

export const addPostAC = (postPost: string) => {
    return {
        type: 'ADD-POST',
        postPost: postPost
    } as const
}
export const onPostChangeAC = (newText: string) => {
    return {
        type: 'UPDATE-NEW-POST-TEXT',
        newText: newText
    } as const
}

export const setUserProfile = (profile: ProfileDaTaType) => {
    return {
        type: 'SET-USER-PROFILE',
        profile
    } as const
}

export const setStatus = (status: string) => {
    return {
        type: 'SET-USER-STATUS',
        status
    } as const
}


export const getUserProfile = (userId: number) => {
    return (dispatch: Dispatch<AddPostType>) => {
        profileAPI.getProfile(userId)
            .then(response => {
                dispatch(setUserProfile(response.data))
            })
    }
}

export const getUserStatus = (userId: number) => {
    return (dispatch: Dispatch<AddPostType>) => {
        profileAPI.getStatus(userId)
            .then(response => {
                dispatch(setStatus(response.data))
            })
    }
}

export const updateStatus = (status: string) => {
    return (dispatch: Dispatch<AddPostType>) => {
        profileAPI.updateStatus(status)
            .then(response => {
                dispatch(setStatus(status))
                if (response.data.resultCode === 0) {
                    dispatch(setStatus(status))
                }
            })
    }
}



